contracts/CarbonCreditToken.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.0;

import "@openzeppelin/contracts/token/ERC20/ERC20.sol";

contract CarbonCreditToken is ERC20 {
    address public admin;

    constructor(uint256 initialSupply) ERC20("CarbonCredit", "CCT") {
        admin = msg.sender; // The deployer is the admin
        _mint(msg.sender, initialSupply); // Mint initial supply to the admin
    }

    function mint(address to, uint256 amount) external {
        require(msg.sender == admin, "Only admin can mint");
        _mint(to, amount);
    }

    function burn(uint256 amount) external {
        _burn(msg.sender, amount); // Allow users to burn their tokens
    }

    function changeAdmin(address newAdmin) external {
        require(msg.sender == admin, "Only admin can change admin");
        admin = newAdmin;
    }
}

contracts/CarbonMarketplace.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.0;

import "./CarbonCreditToken.sol";

contract CarbonMarketplace {
    CarbonCreditToken public carbonToken;
    address public admin;

    struct Listing {
        uint256 amount;
        uint256 pricePerToken;
        address seller;
    }

    mapping(uint256 => Listing) public listings;
    uint256 public nextListingId;

    event Listed(uint256 listingId, address seller, uint256 amount, uint256 pricePerToken);
    event Bought(uint256 listingId, address buyer, uint256 amount);

    constructor(address _carbonToken) {
        carbonToken = CarbonCreditToken(_carbonToken);
        admin = msg.sender;
    }

    function listTokens(uint256 amount, uint256 pricePerToken) external {
        require(carbonToken.balanceOf(msg.sender) >= amount, "Insufficient balance to list");

        // Create a new listing
        listings[nextListingId] = Listing(amount, pricePerToken, msg.sender);
        emit Listed(nextListingId, msg.sender, amount, pricePerToken);
        nextListingId++;
    }

    function buyTokens(uint256 listingId) external payable {
        Listing memory listing = listings[listingId];
        require(msg.value == listing.amount * listing.pricePerToken, "Incorrect ETH amount sent");

        // Transfer tokens from seller to buyer
        carbonToken.transferFrom(listing.seller, msg.sender, listing.amount);
        payable(listing.seller).transfer(msg.value); // Pay the seller

        delete listings[listingId]; // Remove the listing
        emit Bought(listingId, msg.sender, listing.amount);
    }
}

contracts/Governance.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.0;

import "./CarbonCreditToken.sol";

contract Governance {
    CarbonCreditToken public carbonToken;

    struct Proposal {
        string description;
        uint256 voteCount;
        address proposer;
    }

    mapping(uint256 => Proposal) public proposals;
    uint256 public nextProposalId;

    mapping(address => mapping(uint256 => bool)) public votes;

    event ProposalCreated(uint256 proposalId, string description);
    event Voted(uint256 proposalId, address voter);

    constructor(address _carbonToken) {
        carbonToken = CarbonCreditToken(_carbonToken);
    }

    function createProposal(string memory description) external {
        proposals[nextProposalId] = Proposal(description, 0, msg.sender);
        emit ProposalCreated(nextProposalId, description);
        nextProposalId++;
    }

    function vote(uint256 proposalId) external {
        require(carbonToken.balanceOf(msg.sender) > 0, "You must own tokens to vote");
        require(!votes[msg.sender][proposalId], "You have already voted");

        votes[msg.sender][proposalId] = true;
        proposals[proposalId].voteCount += carbonToken.balanceOf(msg.sender);

        emit Voted(proposalId, msg.sender);
    }

    function getProposal(uint256 proposalId) external view returns (Proposal memory) {
        return proposals[proposalId];
    }
}

contracts/Lock.sol.content:
// SPDX-License-Identifier: UNLICENSED
pragma solidity ^0.8.27;

// Uncomment this line to use console.log
// import "hardhat/console.sol";

contract Lock {
    uint public unlockTime;
    address payable public owner;

    event Withdrawal(uint amount, uint when);

    constructor(uint _unlockTime) payable {
        require(
            block.timestamp < _unlockTime,
            "Unlock time should be in the future"
        );

        unlockTime = _unlockTime;
        owner = payable(msg.sender);
    }

    function withdraw() public {
        // Uncomment this line, and the import of "hardhat/console.sol", to print a log in your terminal
        // console.log("Unlock time is %o and block timestamp is %o", unlockTime, block.timestamp);

        require(block.timestamp >= unlockTime, "You can't withdraw yet");
        require(msg.sender == owner, "You aren't the owner");

        emit Withdrawal(address(this).balance, block.timestamp);

        owner.transfer(address(this).balance);
    }
}

